import React from 'react';
import { useNavigate } from 'react-router-dom';
import '../styles/landing.css';

const LandingPage = () => {
  const navigate = useNavigate();

  return (
    <div className="landing-container">
      <div className="overlay">
        <img src="/assets/logo.png" alt="AalimAI Logo" className="logo" />
        <h1 className="landing-title">Welcome to AalimAI</h1>
        <p className="landing-subtitle">Your AI Islamic Guide for Authentic Answers with Hadith References</p>
        <button className="enter-button" onClick={() => navigate('/chat')}>
          Enter
        </button>
      </div>
    </div>
  );
};

export default LandingPage;
