import React, { useState } from "react";
import "./Chat.css";

const Chat = () => {
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");

  const handleAsk = async () => {
    if (!question.trim()) return;

    try {
      const response = await fetch("http://localhost:5000/api/ask", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ question }),
      });

      const data = await response.json();
      setAnswer(data.answer || "کوئی جواب نہیں ملا، دوبارہ کوشش کریں۔");
    } catch (error) {
      setAnswer("سرور سے رابطہ نہیں ہو سکا۔");
      console.error("Error:", error);
    }
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        بِسْمِ ٱللّٰهِ ٱلرَّحْمَٰنِ ٱلرَّحِيمِ
      </div>
      <div className="chat-box">
        <textarea
          className="chat-input"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="اپنا اسلامی سوال لکھیں..."
        />
        <button className="chat-button" onClick={handleAsk}>
          Ask
        </button>
        {answer && <div className="chat-response">{answer}</div>}
      </div>
    </div>
  );
};

export default Chat;
